INSERT INTO `job_postings` (`id`, `title`, `slug`, `departmentId`, `jobType`, `location`, `isRemote`, `description`, `requirements`, `salaryMin`, `salaryMax`, `currency`, `applicationDeadline`, `applicationEmail`, `isActive`, `isFeatured`, `postedAt`, `createdAt`, `updatedAt`) VALUES
('job_001', 'Senior Water Systems Engineer', 'senior-water-systems-engineer', 'dept_001', 'full-time', 'Navi Mumbai, Maharashtra', false, 'We are seeking an experienced Senior Water Systems Engineer to lead design and implementation of clean utility systems. You will work on high-profile projects for pharmaceutical and biotech clients, ensuring compliance with GMP standards and regulatory requirements.', 'Bachelor\'s degree in Chemical/Mechanical Engineering, 5+ years experience in water systems design, knowledge of GMP/GLP standards, strong project management skills', 800000.00, 1200000.00, 'INR', DATE_ADD(NOW(), INTERVAL 30 DAY), 'careers@impact-solutions.com', true, true, NOW(), NOW(), NOW()),
('job_002', 'Sales Manager - Pharmaceutical Sector', 'sales-manager-pharmaceutical-sector', 'dept_002', 'full-time', 'Mumbai, Maharashtra', false, 'Join our dynamic sales team as a Sales Manager focusing on the pharmaceutical sector. You will be responsible for building relationships with key clients, identifying opportunities, and driving revenue growth in the clean utility solutions space.', 'MBA or equivalent, 3+ years B2B sales experience in pharma/industrial sector, excellent communication skills, proven track record of meeting targets', 600000.00, 900000.00, 'INR', DATE_ADD(NOW(), INTERVAL 25 DAY), 'careers@impact-solutions.com', true, true, NOW(), NOW(), NOW()),
('job_003', 'Validation Engineer', 'validation-engineer', 'dept_004', 'full-time', 'Pune, Maharashtra', false, 'We are looking for a Validation Engineer to perform IQ/OQ/PQ validation activities for clean utility systems. This role involves working closely with clients to ensure regulatory compliance and system validation.', 'Bachelor\'s degree in Engineering, 2+ years validation experience, knowledge of FDA/EMA guidelines, attention to detail, excellent documentation skills', 500000.00, 750000.00, 'INR', DATE_ADD(NOW(), INTERVAL 20 DAY), 'careers@impact-solutions.com', true, false, NOW(), NOW(), NOW()),
('job_004', 'Service Technician', 'service-technician', 'dept_003', 'full-time', 'Hyderabad, Telangana', false, 'Join our operations team as a Service Technician. You will be responsible for installation, maintenance, and troubleshooting of water systems and related equipment. Field work and customer interaction required.', 'Diploma in Mechanical/Electrical Engineering, 1+ years field service experience, willingness to travel, strong problem-solving skills', 300000.00, 450000.00, 'INR', DATE_ADD(NOW(), INTERVAL 15 DAY), 'careers@impact-solutions.com', true, false, NOW(), NOW(), NOW()),
('job_005', 'R&D Engineer - Water Treatment', 'rd-engineer-water-treatment', 'dept_005', 'full-time', 'Bangalore, Karnataka', true, 'We are seeking an innovative R&D Engineer to work on developing next-generation water treatment solutions. This role involves research, prototyping, and collaboration with cross-functional teams.', 'Master\'s degree in Chemical/Environmental Engineering, 2+ years R&D experience, knowledge of water treatment technologies, creative problem-solving abilities', 700000.00, 1000000.00, 'INR', DATE_ADD(NOW(), INTERVAL 35 DAY), 'careers@impact-solutions.com', true, true, NOW(), NOW(), NOW()),
('job_006', 'Customer Support Specialist', 'customer-support-specialist', 'dept_006', 'full-time', 'Navi Mumbai, Maharashtra', false, 'Join our customer support team to provide technical assistance and support to our clients. You will handle inquiries, troubleshoot issues, and ensure customer satisfaction.', 'Bachelor\'s degree, excellent communication skills, technical aptitude, customer service experience preferred, ability to work in shifts', 350000.00, 500000.00, 'INR', DATE_ADD(NOW(), INTERVAL 18 DAY), 'careers@impact-solutions.com', true, false, NOW(), NOW(), NOW());
