('evt_001', 'Clean Utility Systems Workshop 2025', 'clean-utility-systems-workshop-2025', 'workshop', 'Join us for an intensive workshop on clean utility systems design, validation, and maintenance. Learn from industry experts about best practices, regulatory compliance, and emerging technologies in water purification and distribution systems.', DATE_ADD(NOW(), INTERVAL 15 DAY), DATE_ADD(NOW(), INTERVAL 16 DAY), 'Asia/Kolkata', 'Taj Lands End', 'Bandra Kurla Complex, Bandra West, Mumbai, Maharashtra 400050', 'Mumbai', false, NULL, 'https://images.unsplash.com/photo-1522071820081-009f0129c71c?w=1200&q=80', true, 'https://impact-solutions.com/events/register/workshop-2025', DATE_ADD(NOW(), INTERVAL 10 DAY), 100, 'upcoming', true, true, NOW(), NOW()),
('evt_002', 'Pharmaceutical Water Quality Webinar', 'pharmaceutical-water-quality-webinar', 'webinar', 'Free webinar covering pharmaceutical water quality standards, monitoring techniques, and compliance requirements. Interactive Q&A session with industry experts.', DATE_ADD(NOW(), INTERVAL 7 DAY), DATE_ADD(NOW(), INTERVAL 7 DAY), 'Asia/Kolkata', NULL, NULL, NULL, true, 'https://zoom.us/j/webinar-pharma-water', 'https://images.unsplash.com/photo-1522202176988-66273c2fd55f?w=1200&q=80', true, 'https://impact-solutions.com/events/register/webinar-pharma', DATE_ADD(NOW(), INTERVAL 5 DAY), 500, 'upcoming', true, true, NOW(), NOW()),
('evt_003', 'Product Launch: Next-Gen TOC Analyzer', 'product-launch-next-gen-toc-analyzer', 'product-launch', 'Exclusive launch event for our latest MINI TOC 3 analyzer with advanced features. Live demonstrations, technical sessions, and special launch pricing available.', DATE_ADD(NOW(), INTERVAL 30 DAY), DATE_ADD(NOW(), INTERVAL 30 DAY), 'Asia/Kolkata', 'ITC Maratha', 'Sahar, Andheri East, Mumbai, Maharashtra 400099', 'Mumbai', false, NULL, 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=1200&q=80', true, 'https://impact-solutions.com/events/register/toc-launch', DATE_ADD(NOW(), INTERVAL 25 DAY), 150, 'upcoming', true, true, NOW(), NOW()),
